            hintItems += '<div>' + hints[i] + '</div>';
          }
        }

        if (hintsLength > 70 && hintsLength <= 100) {
          hintsClass += ' hints-long';
        }

        if (hintsLength > 100) {
          hintsClass += ' hints-super-long';
        }

        hintsHtml = '<div id="hintList" class="' + hintsClass + '">';
        hintsHtml += hintItems;
        hintsHtml += '</div>';
        return hintsHtml;
