                ]
            }
        },

        "reply": {
            "background": {
                "color": [
                    'rgba(122, 40, 255, 0.95)',
                    'rgba(61, 122, 255, 0.95)'
                ]
            },
            "hover": {
                "color": [
                    'rgba(122, 40, 255, 0.95)',
                    'rgb(122, 40, 255)'
                ]
            },
