    };
    btnOval1.style.minHeight = btnSize / ovalCoefficient + 'px';
    btnOval1.style.height = btnSize / ovalCoefficient + 'px';
    btnOval1.style.maxHeight = btnSize / ovalCoefficient + 'px';
    btnOval1.style.minWidth = btnSize + 'px';
