import React from 'react';
import { Grid, Grow, Typography } from '@material-ui/core';
import useStyles from './styles.js';
import NewsCard from '../NewsCard/NewsCard';


const infoCards = [
    { color: '#00838f', title: 'Latest News', text: 'Give me the latest news' },
    { color: '#1565c0', title: 'News by Categories', info: 'Business, Entertainment, General, Health, Science, Sports, Technology', text: 'Give me the latest Technology news' },
    { color: '#4527a0', title: 'News by Terms', info: 'Bitcoin, PlayStation 5, Smartphones, Donald Trump...', text: 'What\'s up with PlayStation 5' },
    { color: '#283593', title: 'News by Sources', info: 'CNN, Wired, BBC News, Time, IGN, Buzzfeed, ABC News...', text: 'Give me the news from CNN' },
];


function NewsCards({ articles }) {
    const classes = useStyles();

    if (!articles.length) {
        return (
            <Grow in>

                <Grid className={classes.container} container alignItems="stretch" spacing={4}>
                    {infoCards.map((infoCard) => (
                        <Grid item xs={12} sm={6} lg={3} className={classes.infoCard}>
                            <div className={classes.card} style={{ backgroundColor: infoCard.color }}>
                                <Typography varient="h5">{infoCard.title}</Typography>
                                {infoCard.info ? (<Typography varient="h6"><strong>{infoCard.title.split(' ')[2]}:</strong>
                                    <br />
                                    {infoCard.info}
                                </Typography>) : null}

                                <Typography varient="h6">Try sying: <br/></Typography>
                            </div>
                        </Grid>
                    ))}
                </Grid>
            </Grow>
        );
    }
    return (
        <Grow in>

            <Grid className={classes.container} container alignItems="stretch" spacing={4}>

                {articles.map((article, i) => (
                    <Grid item xs={12} sm={6} lg={4} style={{ display: 'flex' }}>
                        <NewsCard article={article} i={i} />
                    </Grid>
                ))}


            </Grid>

        </Grow>
    )
}

export default NewsCards
